package com.fld.framework.exception;import com.fld.framework.entity.BaseStatus;public class ApiException extends ActionException {	/**	 * 	 */	private static final long serialVersionUID = 8308975371215774642L;	public ApiException() {	}	public ApiException(BaseStatus status) {		super(status.getCode(), status.getMsg());	}	public ApiException(int code, String msg) {		super(code, msg);	}	public ApiException(String message) {		super(message);	}	public ApiException(Throwable cause) {		super(cause);	}	public ApiException(String message, Throwable cause) {		super(message, cause);	}}