package com.fld.framework.exception;import com.fld.framework.entity.BaseStatus;public class MwebException extends ActionException {	/**	 * 	 */	private static final long serialVersionUID = 3998626235447725950L;	public MwebException() {	}	public MwebException(BaseStatus status) {		super(status.getCode(), status.getMsg());	}	public MwebException(int code, String msg) {		super(code, msg);	}	public MwebException(String message) {		super(message);	}	public MwebException(Throwable cause) {		super(cause);	}	public MwebException(String message, Throwable cause) {		super(message, cause);	}	public static void throwException(BaseStatus status) {		throw new MwebException(status);	}}