package com.fld.framework.exception;import com.fld.framework.entity.BaseStatus;public class WebException extends ActionException {	/** 	*/	private static final long serialVersionUID = 1L;	public WebException() {	}	public WebException(BaseStatus status) {		super(status.getCode(), status.getMsg());	}	public WebException(int code, String msg) {		super(code, msg);	}	public WebException(String message) {		super(message);	}	public WebException(Throwable cause) {		super(cause);	}	public WebException(String message, Throwable cause) {		super(message, cause);	}	public static void throwException(BaseStatus status) {		throw new WebException(status);	}}