package com.fld.init.framework.dynamic;public class JdbcContextHolder {	private static final ThreadLocal<Object> contextHolder = new ThreadLocal<Object>();	public final String MASTER = "master";	public final String SLAVE = "slave";	private void setJdbcType(Object jdbcType) {		contextHolder.set(jdbcType);	}	public void setSlave() {		clearJdbcType();		setJdbcType(SLAVE);	}	public void setMaster() {		clearJdbcType();		setJdbcType(MASTER);	}	public Object getJdbcType() {		return contextHolder.get();	}	public Object getMasterJdbcType() {		this.setMaster();		return contextHolder.get();	}	public void clearJdbcType() {		contextHolder.remove();	}}