package com.fld.init.framework.util;import org.apache.commons.lang3.StringUtils;import com.fld.framework.util.GenerateUtil;import com.fld.framework.util.MD5Util;/** * 密码加密生产工具类 * */public class EncryptUtil {	/**	 * @Title 密码加密	 * @Description 生成密码方式 0为系统本身使用	 * @param pwd	 * @param salt	 * @return	 */	public static final String encodePwd(String pwd, String salt) {		if (!StringUtils.isBlank(pwd) && !StringUtils.isBlank(salt)) {			return MD5Util.MD5(MD5Util.MD5(pwd).concat(salt));		}		return "";	}	/**	 * 生成4位含有数字大小写字母的随机数	 * 	 * @return	 */	public static final String genSalt() {		return GenerateUtil.getRandomStr(4);	}	/**	 * 生成10位含有数字大小写字母的随机数	 * 	 * @return	 */	public static final String genPassword() {		return GenerateUtil.getRandomStr(10);	}}